operation.add:

//cod fee code start	
		if (isset($this->session->data['shipping_method'])) {
			$shipping_method = $this->session->data['shipping_method']['code'];
		} else {
			$shipping_method = '';
		}

		if(!empty($this->config->get('tmdcodfee_delivery'))){
			$restrict_delivery=$this->config->get('tmdcodfee_delivery');
		}else{
			$restrict_delivery=array();
		}	

		if(in_array($shipping_method, $restrict_delivery)){
		$status = false;
		}else{
		$status = true;
		}

//cod fee code end
